import{_ as a,c as t,o as e,aa as r}from"./chunks/framework.CAVwB9kQ.js";const o="/Lyc-notes/assets/%E7%AC%AC%E4%B8%83%E7%AB%A0%E6%A6%82%E8%BF%B0.Bpq3KyCw.png",m=JSON.parse('{"title":"第 7 章 查找","description":"","frontmatter":{},"headers":[],"relativePath":"dataStructure/chapter7.md","filePath":"dataStructure/chapter7.md","lastUpdated":1719146550000}'),s={name:"dataStructure/chapter7.md"},n=r('<h1 id="第-7-章-查找" tabindex="-1">第 7 章 查找 <a class="header-anchor" href="#第-7-章-查找" aria-label="Permalink to &quot;第 7 章 查找&quot;">​</a></h1><p><img src="'+o+'" alt="alt text"></p><h2 id="查找的基本概念" tabindex="-1">查找的基本概念 <a class="header-anchor" href="#查找的基本概念" aria-label="Permalink to &quot;查找的基本概念&quot;">​</a></h2><p>（1）<strong>查找</strong> （2）<strong>查找表</strong> （3）<strong>静态查找表&amp;动态查找表</strong>：若一个查找表的操作只涉及查找操作，则无须动态地修改查找表，此类查找表称为静态查找表。与此对应，需要动态地插入或删除的查找表称为动态查找表。适合静态查找表的查找方法有顺序查找、折半查找、散列查找等;适合动态查找表的查找方法有二叉排序树的查找、散列查找等 （4）<strong>关键字</strong> （5）<strong>平均查找长度</strong>：一次查找的长度是指需要比较的关键字次数，而平均查找长度则是所有查找过程中进行关键字的比较次数的平均值</p><h2 id="顺序查找和折半查找" tabindex="-1">顺序查找和折半查找 <a class="header-anchor" href="#顺序查找和折半查找" aria-label="Permalink to &quot;顺序查找和折半查找&quot;">​</a></h2><h4 id="顺序查找" tabindex="-1">顺序查找 <a class="header-anchor" href="#顺序查找" aria-label="Permalink to &quot;顺序查找&quot;">​</a></h4><p>顺序查找又称线性查找，它对顺序表和链表都是适用的。对于顺序表，可通过数组下标递增 来顺序扫描每个元素:对于链表，可通过指针 next 来依次扫描每个元素。顺序查找通常分为对一般的无序线性表的顺序查找和对按关键字有序的线性表的顺序查找</p><ol><li><p><strong>一般线性表的顺序查找</strong></p></li><li><p><strong>有序线性表的顺序查找</strong></p></li></ol><h4 id="折半查找" tabindex="-1">折半查找 <a class="header-anchor" href="#折半查找" aria-label="Permalink to &quot;折半查找&quot;">​</a></h4><p>二分查找</p>',10),c=[n];function i(l,h,d,p,_,u){return e(),t("div",null,c)}const f=a(s,[["render",i]]);export{m as __pageData,f as default};
